    logic signed [N-1:0] segment_slope;
    logic signed [N-1:0] segment_intercept;

    always_comb begin
        segment_slope = 0;
        segment_intercept = 0;

        // Clamp input outside range
        if (x < breakpoints[0]) begin
            y = 0;  // Sigmoid approaches 0 for -inf
        end else if (x >= breakpoints[256]) begin
            y = (1 << R);  // Sigmoid approaches 1 for +inf (1.0 in fixed-point)
        end else begin
            // Find the appropriate segment
            for (int i = 0; i < 256; i++) begin
                if (x >= breakpoints[i] && x < breakpoints[i + 1]) begin
                    segment_slope = slope[i];
                    segment_intercept = intercept[i];
                end
            end
            // Calculate the output
            y = ((segment_slope * x) >>> R) + segment_intercept;
        end
    end
endmodule